mapper.select:
SELECT * FROM book WHERE ISBN LIKE CONCAT('%', #{book.ISBN}, '%') AND name LIKE CONCAT('%', #{book.name}, '%') AND author LIKE CONCAT('%', #{book.author}, '%') AND press LIKE CONCAT('%', #{book.press}, '%') AND type LIKE CONCAT('%', #{book.type}, '%')
AND pricing =#{book.pricing}
AND number =#{book.number}
AND borrow=#{book.borrow}
AND introduction LIKE CONCAT('%', #{book.introduction}, '%')

select COUNT(1) FROM book WHERE ISBN LIKE CONCAT('%', #{book.ISBN}, '%') AND name LIKE CONCAT('%', #{book.name}, '%') AND author LIKE CONCAT('%', #{book.author}, '%') AND press LIKE CONCAT('%', #{book.press}, '%') AND type LIKE CONCAT('%', #{book.type}, '%')
AND pricing =#{book.pricing}
AND number =#{book.number}
AND borrow=#{book.borrow}
AND introduction LIKE CONCAT('%', #{book.introduction}, '%')
